const path = require('path')
const walkthrough = require('./walkthrough')

describe('test directory walkthrough', () => {
  test('shallow directory walkthrough', () => {
    const currentDir = path.resolve(__dirname)
    const currentDirFiles = walkthrough(currentDir)
    expect(currentDirFiles).toEqual([
      currentDir + '/walkthrough.js',
      currentDir + '/walkthrough.test.js'
    ])
  })
})
